const getOutputOption = (output) => {
    if (output === 'tensor') {
        return 'tensor';
    }
    return 'base64';
};
export const getUpscaleOptions = ({ output, progressOutput, ...options } = {}) => {
    return {
        ...options,
        output: getOutputOption(output),
        progressOutput: getOutputOption(progressOutput || output),
    };
};
